package com.example.yemekuygulamasiimport android.content.Intentimport android.view.LayoutInflaterimport android.view.Viewimport android.view.ViewGroupimport android.widget.ImageViewimport android.widget.TextViewimport androidx.recyclerview.widget.RecyclerViewclass MainCoursesAdapter(private var itemList: List<Meal>) :    RecyclerView.Adapter<MainCoursesAdapter.ViewHolder>() {    fun filterData(newList: List<Meal>) {        itemList = newList        notifyDataSetChanged()    }    override fun onCreateViewHolder(        parent: ViewGroup,        viewType: Int    ): ViewHolder {        val view = LayoutInflater.from(parent.context)            .inflate(R.layout.list_item, parent, false)        return ViewHolder(view)    }    override fun onBindViewHolder(holder: ViewHolder, position: Int) {        val item = itemList[position]        holder.bind(item)    }    override fun getItemCount(): Int {        return itemList.size    }    inner class ViewHolder(itemView: View) : RecyclerView.ViewHolder(itemView) {        private val imageView: ImageView = itemView.findViewById(R.id.imageView)        private val textView: TextView = itemView.findViewById(R.id.textView)        init {            itemView.setOnClickListener {                val position = adapterPosition                if (position != RecyclerView.NO_POSITION) {                    val item = itemList[position]                    val intent = Intent(                        itemView.context,                        YemekTarifiActivity::class.java                    )                    intent.putExtra("mealName", item.mealName)                    intent.putExtra("mealImageResId", item.mealImgRes)                    intent.putExtra("mealDescription", item.mealDescription)                    itemView.context.startActivity(intent)                }            }        }        fun bind(item: Meal) {            imageView.setImageResource(item.mealImgRes)            textView.text = item.mealName        }    }}